oozeContract = web3.eth.contract(address=oozeAddress, abi=oozeAbi)
slimeContract = web3.eth.contract(address=slimeAddress, abi=slimeAbi)


def oozePrice():
    return round(web3.fromWei(slimeContract.functions.getTokenToCollateralInputPrice(1000000000000000000).call(), 'mwei'), 2)


def totalSlime():
    return slimeContract.functions.totalSupply().call()


def oozeBalance(address):
    address = web3.toChecksumAddress(address)
    return web3.fromWei(oozeContract.functions.balanceOf(address).call(), 'ether')