def calcPCSV1Price(tokenAddress):
    BUSDTokenAddress = "0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56"
    WBNBTokenAddress = "0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c"
    amountIn = 100000000
    amountOut = pcsV1RouterContract.functions.getAmountsOut(amountIn, [tokenAddress, WBNBTokenAddress, BUSDTokenAddress]).call()
    amountOut = web3.fromWei(amountOut[2], "ether")
    return amountOut


def calcPCSV2Price(tokenInAddress, tokenOutAddress):
    amountIn = web3.toWei("1", "ether")
    amountOut = pcsV2RouterContract.functions.getAmountsIn(amountIn, [tokenOutAddress, tokenInAddress]).call()
    amountOut = web3.fromWei(amountOut[0], "ether")
    return amountOut